mapper.insert:
insert into construction_industry_api.t_enterprise_resource
( id
, is_shared
, enterprise_name
, enterprise_qualifications
, area
, contact_address
, contacts
, sex
, telephone_number
, qq_number
, customer_type
, follow_up_situation
, creator_id
, gmt_create
, regenerator_id
, gmt_modified
, remark)
values ( #{id}
       , #{shared}
       , #{enterpriseName}
       , #{enterpriseQualifications}
       , #{area}
       , #{contactAddress}
       , #{contacts}
       , #{sex}
       , #{telephoneNumber}
       , #{qqNumber}
       , #{customerType}
       , #{followUpSituation}
       , #{creatorId}
       , #{gmtCreate}
       , #{regeneratorId}
       , #{gmtModified}
       , #{remark})

mapper.select:
select t1.id, t1.is_shared, t1.enterprise_name, t1.enterprise_qualifications, t1.area, t1.contact_address, t1.contacts, t1.sex, t1.telephone_number, t1.qq_number, t1.customer_type, t1.follow_up_situation, t1.remark, t1.creator_id, t1.gmt_create, t1.regenerator_id, t1.gmt_modified, t2.id, t2.enterprise_resource_id, t2.level_major_initial_conversion, t2.gmt_create as gmt_create2, t2.creator_id as creator_id2, t2.regenerator_id as regenerator_id2, t2.gmt_modified as gmt_modified2 from t_enterprise_resource as t1 left join t_enterprise_resource_demand as t2 on t1.id = t2.enterprise_resource_id where 1 = 1
and enterprise_name like #{search.enterpriseName}
and customer_type = #{search.customerType}
and t1.is_shared = #{search.shared}
and t1.creator_id = #{search.creatorId}
and t1.gmt_create >= #{search.startDate}
and t1.gmt_create <= #{search.endDate}
and t1.id in ( select distinct (enterprise_resource_id) from t_enterprise_resource_demand where json_contains(t2.level_major_initial_conversion ->> '$[*].levelMajor',json_array(#{search.levelMajor}),'$') )
group by t1.id order by t1.id desc limit #{page.currentPage},#{page.pageSize}

select count(*) from t_enterprise_resource
where 1 = 1


    and enterprise_name like #{search.enterpriseName}


    and customer_type = #{search.customerType}


    and is_shared = #{search.shared}


    and creator_id = #{search.creatorId}


    and gmt_create >= #{search.startDate}


    and gmt_create <= #{search.endDate}


    and id in (
    select distinct (enterprise_resource_id)
    from t_enterprise_resource_demand
    where json_contains(level_major_initial_conversion ->>
    '$[*].levelMajor',json_array(#{search.levelMajor}),'$')
    )
