mapper.select:
select id, remark, status, transfer_customers, area, category_and_grade, acquisition_amount, safety_permit, construction_progress, qualified_personnel, gmt_create, creator_id, gmt_modified, regenerator_id, acquisition_date from construction_industry_api.t_qualification_acquisition where status = 1
and id is not null
and id != #{selectedTransferCustomer}
and json_contains(category_and_grade, #{categoryAndGrade}) order by id desc limit #{page.currentPage},#{page.pageSize}

select count(*) from t_qualification_transfer
where 1 = 1


    and transfer_customers like #{search.transferCustomers}


    and json_contains(qualification_requirements,json_array(#{search.qualificationRequirements}))


    and area in(

        #{item}
    )


    and status = #{search.status}


    and creator_id = #{search.creatorId}


    and gmt_create >= #{search.startDate}


    and gmt_create <= #{search.endDate}


    and is_deleted = #{search.deleted}

select id, transfer_customers, qualification_requirements, area, status, creator_id, gmt_create, regenerator_id,
gmt_modified, remark
from t_qualification_transfer
where 1 = 1


    and transfer_customers like #{search.transferCustomers}


    and json_contains(qualification_requirements,json_array(#{search.qualificationRequirements}))


    and area in(

        #{item}
    )


    and status = #{search.status}


    and creator_id = #{search.creatorId}


    and gmt_create >= #{search.startDate}


    and gmt_create <= #{search.endDate}


    and is_deleted = #{search.deleted}

order by id
limit #{page.currentPage},#{page.pageSize}
